msg.filename = msg.payload.filename;
msg.fileid = msg.payload.fileid;
msg.query = { "_id": msg.fileid}
return msg;

if(msg.type===null || msg.type===undefined)
{
    msg.type = 'LABEL_DETECTION';
    msg.type = 'OBJECT_LOCALIZATION';
}
msg.maxresults = 5;
if(msg.payload.maxresults !== null &&msg.payload.maxresults !== undefined)
{
    msg.maxresults = msg.payload.maxresults;
}

var image = {content: msg.payload.toString('base64')};
var features = {type: msg.type, maxResults: msg.maxresults};
var imageContext = {languageHints: 'ja'};
var request = {image: image, features: features, imageContext: imageContext};
var requests = {requests: request};
msg.payload = requests;

msg.method = "POST";
msg.url = "https://vision.googleapis.com/v1/images:annotate"

return msg;

// To skip messages like { "responses": [ {} ] }
if (Object.keys(msg.payload.responses[0]).length < 1) {
    msg.payload = { labels: [], labelarray: [] };
    return msg;
}

var labelarray = [];
var labels = 'labels: ';
var labelAnnotations = [];
if(msg.type == 'LABEL_DETECTION') {
    labelAnnotations = msg.payload.responses[0].labelAnnotations;
    for (var i = 0; i < labelAnnotations.length; i++) {
        labels += labelAnnotations[i].description;
        labels +=' (' + labelAnnotations[i].score + '), ';
        labelarray.push({name: labelAnnotations[i].description, score: labelAnnotations[i].score});
    }
} else if (msg.type == 'OBJECT_LOCALIZATION')
{
    labelAnnotations = msg.payload.responses[0].localizedObjectAnnotations;
    for (var i = 0; i < labelAnnotations.length; i++) {
        labels += labelAnnotations[i].name;
        labels +=' (' + labelAnnotations[i].score + '), ';
        labelarray.push({name: labelAnnotations[i].name, score: labelAnnotations[i].score});
    }

}


msg.payload = {labels: labels.slice(0, labels.length - 2), labelarray: labelarray};
return msg;
